mapper.insert:
INSERT INTO social.activity_session (
    id, activity_id, title, start_time, end_time, capacity, status, created_at, updated_at
) VALUES (
    #{id}, #{activityId}, #{title}, #{startTime}, #{endTime}, #{capacity}, #{status}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE social.activity_session
title = #{title}, start_time = #{startTime}, end_time = #{endTime}, capacity = #{capacity}, status = #{status}, updated_at = #{updatedAt}
WHERE id = #{id}
