mapper.select:
SELECT id, account, channel, purpose, code_hash, expires_at, attempts, created_at
FROM shared.verification_code
WHERE account = #{account, jdbcType=VARCHAR}
  AND purpose = #{purpose, jdbcType=INTEGER}
  AND expires_at > #{now, jdbcType=TIMESTAMP}
ORDER BY created_at DESC
LIMIT 1

mapper.update:
UPDATE shared.verification_code
SET attempts = attempts + 1
WHERE id = #{id, jdbcType=OTHER, typeHandler=org.apache.ibatis.type.UUIDTypeHandler}
