mapper.select:
SELECT user_id, task_date, task_id, progress, target, status, updated_at
FROM social.daily_task_progress
WHERE user_id = #{userId, jdbcType=OTHER, typeHandler=com.youthloop.common.mybatis.UuidTypeHandler}
  AND task_date = #{taskDate, jdbcType=DATE}

SELECT user_id, task_date, task_id, progress, target, status, updated_at
FROM social.daily_task_progress
WHERE user_id = #{userId, jdbcType=OTHER, typeHandler=com.youthloop.common.mybatis.UuidTypeHandler}
  AND task_date = #{taskDate, jdbcType=DATE}
  AND task_id = #{taskId, jdbcType=OTHER, typeHandler=com.youthloop.common.mybatis.UuidTypeHandler}

mapper.update:
UPDATE social.daily_task_progress
SET progress = #{progress, jdbcType=INTEGER},
    status = #{status, jdbcType=INTEGER},
    updated_at = #{updatedAt, jdbcType=TIMESTAMP}
WHERE user_id = #{userId, jdbcType=OTHER, typeHandler=com.youthloop.common.mybatis.UuidTypeHandler}
  AND task_date = #{taskDate, jdbcType=DATE}
  AND task_id = #{taskId, jdbcType=OTHER, typeHandler=com.youthloop.common.mybatis.UuidTypeHandler}
