mapper.select:
SELECT c.id, c.type, c.title, c.summary, c.cover_url, c.published_at, c.status, c.created_at, COALESCE(s.like_count, 0) AS like_count, COALESCE(s.fav_count, 0) AS fav_count, COALESCE(s.comment_count, 0) AS comment_count, COALESCE(s.hot_score, 0) AS hot_score FROM social.content c LEFT JOIN social.content_stats s ON c.id = s.content_id WHERE 1=1
AND c.type = #{type}
AND c.status = #{status}
ORDER BY c.published_at DESC NULLS LAST, c.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM social.content c
WHERE 1=1

    AND c.type = #{type}


    AND c.status = #{status}
